    <div class="card" >
        <div class="card-body" >
          <div class="card-body" >
              {{f}}
              <input type="text" [(ngModel)]="keyword" >
              <button class="btn btn-outline-success" >Search
                <i class="bi bi-search" ></i>
              </button>

          </div>
            <table class="table" >
                <thead>
                    <tr>
                        <th>id</th>
                        <th>Name</th>
                        <th>Price</th>
                        <th>Checked</th>
                      <th>Action</th>
                    </tr>
                </thead>
                <tbody>
                    <tr *ngFor="let product of products" >
                        <td>{{product.id}}</td>
                        <td>{{product.name}}</td>
                        <td>{{product.price}}</td>
                        <td>
                            <button (click)="handleCheckProduct(product)" class="btn btn-outline-success">
                                <i [class]="product.checked?'bi bi-check':'bi-circle'" ></i>
                            </button>>
                        </td>
                      <td>
                        <button (click)="handleDeleteProduct(product)" class="btn btn-outline-danger">
                          <i class="bi bi-trash" ></i>
                        </button>>
                      </td>
                    </tr>
                </tbody>
            </table>

        </div>
    </div>
</div>
