import {Component, OnInit} from '@angular/core';
import {HttpClient} from "@angular/common/http";
import {ProductService} from "../services/product.service";
import {Product} from "../model/product.model";
import {Observable} from "rxjs";


@Component({
  selector: 'app-products',
  templateUrl: './products.component.html',
  styleUrls: ['./products.component.css']
})
export class ProductsComponent implements OnInit{

  products$:Observable<Array<Product>> ;

  constructor(private ps:ProductService) {

  }
  handleCheckProduct(product:Product){
    this.ps.checkProduct(product).subscribe(
      {
        next:updatedProduct=>{
         this.products.map(p=>{
           product.checked = !product.checked;
         })
        }
      }
    )

  }
  handleDeleteProduct(product:Product){
    /*this.ps.checkProduct(product).subscribe(
      {
        next:updatedProduct=>{
         this.products.map(p=>{
           product.checked = !product.checked;
         })
        }
      }
    )*/
    this.ps.deleteProduct(product);
  }
  ngOnInit(): void {
    this.getProducts();
  }
  getProducts(){
    this.products$ = this.ps.getProducts();
  }
}
<div class="p-3" >
    <div class="card" >
        <div class="card-body" >
            <table class="table" >
                <thead>
                    <tr>
                        <th>id</th>
                        <th>Name</th>
                        <th>Price</th>
                        <th>Checked</th>
                      <th>Action</th>
                    </tr>
                </thead>
                <tbody>
                    <tr *ngFor="let product of products | async" >
                        <td>{{product.id}}</td>
                        <td>{{product.name}}</td>
                        <td>{{product.price}}</td>
                        <td>
                            <button (click)="handleCheckProduct(product)" class="btn btn-outline-success">
                                <i [class]="product.checked?'bi bi-check':'bi-circle'" ></i>
                            </button>>
                        </td>
                      <td>
                        <button (click)="handleDeleteProduct(product)" class="btn btn-outline-danger">
                          <i class="bi bi-trash" ></i>
                        </button>>
                      </td>
                    </tr>
                </tbody>
            </table>

        </div>
    </div>
</div>
