import {Component, OnInit} from '@angular/core';
import {HttpClient} from "@angular/common/http";
import {ProductService} from "../services/product.service";
import {Product} from "../model/product.model";
import {Observable} from "rxjs";
import {Router} from "@angular/router";


@Component({
  selector: 'app-products',
  templateUrl: './products.component.html',
  styleUrls: ['./products.component.css']
})
export class ProductsComponent implements OnInit{

  products:Array<Product> =[];
  public keyword:string="";
  totalPages:number=0;
  pageSize=3;

  currentPage:number=1;
  constructor(private ps:ProductService,private router:Router) {

  }
  handleCheckProduct(product:Product){
    this.ps.checkProduct(product).subscribe(
      {
        next:updatedProduct=>{

           product.checked = !product.checked;

        }
      }
    )

  }
  handleDeleteProduct(product:Product){
    if(confirm("Etes-vous sure?")){
      this.ps.deleteProduct(product).subscribe(
        {
          next:value=>{
            this.products=this.products.filter(p=>p.id!=product.id)
          }
        }
      )
    }


  }
  ngOnInit(): void {
    this.searchProducts();
  }
  searchProducts(){
   this.ps.searchProducts(this.keyword,this.currentPage,this.pageSize).subscribe(
     {
       next:(resp)=>{
         this.products=resp.body as Product[];
         let totalProducts:number =parseInt(resp.headers.get('x-total-count')!);
         console.log(totalProducts);
         this.totalPages=Math.floor(totalProducts/this.pageSize);

         if(totalProducts % this.pageSize!=0){
          this.totalPages++;
         }
         console.log(this.totalPages);
       },
       error:err => {
        console.log(err);
       }
     }
   )
  }

  /*searchProducts() {
    this.currentPage=1;
    this.totalPages=0;
    this.ps.getProducts(this.keyword,this.currentPage,this.pageSize).subscribe(
      {
        next:data=>{
          this.products=data;
        }
      }
    )
  }*/
  handleGoToPage(page:number){
  this.currentPage=page;
  this.searchProducts()
  }

  handleEditProduct(product: Product) {
    this.router.navigateByUrl("/editProduct/")
  }
}
<div class="p-3" >
    <div class="card" >
        <div class="card-body" >
          <div class="card-body" >
              <input class="p-1" type="text" [(ngModel)]="keyword" >
              <button (click)="searchProducts()" class="btn btn-outline-success ms-1" >
                <i class="bi bi-search" ></i>
              </button>

          </div>
            <table class="table" >
                <thead>
                    <tr>
                        <th>id</th>
                        <th>Name</th>
                        <th>Price</th>
                        <th>Checked</th>
                      <th>Action</th>
                    </tr>
                </thead>
                <tbody>
                    <tr *ngFor="let product of products" >
                        <td>{{product.id}}</td>
                        <td>{{product.name}}</td>
                        <td>{{product.price}}</td>
                        <td>
                            <button (click)="handleCheckProduct(product)" class="btn btn-outline-success">
                                <i [class]="product.checked?'bi bi-check':'bi-circle'" ></i>
                            </button>>
                        </td>
                      <td>
                        <button (click)="handleDeleteProduct(product)" class="btn btn-outline-danger">
                          <i class="bi bi-trash" ></i>
                        </button>>
                      </td>
                      <td>
                        <button (click)="handleEditProduct(product)" class="btn btn-outline-danger">
                          <i class="bi bi-pencil" ></i>
                        </button>>
                      </td>
                    </tr>
                </tbody>
            </table>
            <ul class="nav nav-pills">
              <li *ngFor="let page of [].constructor(this.totalPages);let i=index" >
                <button (click)="handleGoToPage(i+1)"
                        [ngClass]="currentPage==(i+1)?'btn btn-success m-1':'btn btn-outline-success m-1'"
                        >
                    {{i+1}}
                </button>
              </li>
            </ul>
        </div>
    </div>
</div>
