import {Component, OnInit} from '@angular/core';
import {HttpClient} from "@angular/common/http";


@Component({
  selector: 'app-products',
  templateUrl: './products.component.html',
  styleUrls: ['./products.component.css']
})
export class ProductsComponent implements OnInit{

  products:Array<any>=[];

  constructor(private http: HttpClient) {

  }
  handleCheckProduct(product:any){
    this.http.patch<any>(`http://localhost:8089/products/${product.id}`,{checked:!product.checked}).subscribe(
      {
        next:updatedProduct=>{
         this.products.map(p=>{
           if(p.id==product.id){
             return  updatedProduct;
           }else return p;
         })
        }
      }
    )

  }

  ngOnInit(): void {
    this.http.get<Array<any>>("http://localhost:8089/products")
      .subscribe({
        next : data=>this.products=data,
        error :err => {
          console.log(err);
        }
      })
  }
}
<div class="p-3" >
    <div class="card" >
        <div class="card-body" >
            <table class="table" >
                <thead>
                    <tr>
                        <th>id</th>
                        <th>Name</th>
                        <th>Price</th>
                        <th>Checked</th>
                    </tr>
                </thead>
                <tbody>
                    <tr *ngFor="let product of products" >
                        <td>{{product.id}}</td>
                        <td>{{product.name}}</td>
                        <td>{{product.price}}</td>
                        <td>
                            <button (click)="handleCheckProduct(product)" class="btn btn-outline-success">
                                <i [class]="product.checked?'bi bi-check':'bi-circle'" ></i>
                            </button>>
                        </td>
                    </tr>
                </tbody>
            </table>

        </div>
    </div>
</div>
