import {Component, OnInit} from '@angular/core';
import {FormBuilder, FormGroup, Validators} from "@angular/forms";
import {ProductService} from "../services/product.service";

@Component({
  selector: 'app-new-product',
  templateUrl: './new-product.component.html',
  styleUrls: ['./new-product.component.css']
})
export class NewProductComponent implements OnInit{
  public productForm!:FormGroup;
  constructor(private fb:FormBuilder,private productService:ProductService){

  }

  ngOnInit(): void {
    this.productForm=this.fb.group(
      {
        name:this.fb.control('',[Validators.required]),
        price:this.fb.control(0,[Validators.required]),
        checked:this.fb.control(false),
      }
    )
  }
  saveProduct(){
    let product = this.productForm.value;
    this.productService.saveProduct(product).subscribe(
      {
        next:data=>{
          alert(JSON.stringify(data))
        },
        error:err => {
        console.log(err);
        }
      }
    );
  }
}
<div class="p-3" >
    <div class="card" >
        <div class="card-body" >
          <div class="card-body" >
            <form>
              <input type="text"  >
            </form>
          </div>
           <div class="row" >
              <div class="col-md-6" >
                <form [formGroup]="productForm" (ngSubmit)="saveProduct()" >
                <div class="mb-3" >
                <label class="form-label" >Name</label>
                <input class="form-control" formControlName="name">
                </div>

                  <div class="mb-3" >
                    <label class="form-label" >Price</label>
                    <input class="form-control" formControlName="price">
                  </div>

                  <div class="mb-3" >
                    <label class="form-label" >Checked</label>
                    <input type="checkbox" class="form-check-input" formControlName="checked">
                  </div>

                  <button [disabled]="productForm.invalid" class="btn btn-success" >Save</button>
                </form>
              </div>
           </div>
        </div>
    </div>
</div>
